import React, { useState, useEffect } from "react";
import {
  Tabs,
  Tab,
  Paper,
  Table,
  TableRow,
  TableHead,
  TableBody,
  TableCell,
  Chip
} from "@material-ui/core";
import { makeStyles } from "@material-ui/styles";
import SearchBar from "material-ui-search-bar";

// components
import PageTitle from "../../components/PageTitle/PageTitle";


const useStyles = makeStyles(theme => ({
  tableOverflow: {
    overflow: 'auto'
  },
  formContainer: {
    width: "100%",
    height: "20%",
    display: "block",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    [theme.breakpoints.down("md")]: {
      width: "50%",
    },
  },
  success: {
    backgroundColor: theme.palette.success.main,
    color: '#fff',
  },
  warning: {
    backgroundColor: theme.palette.warning.main,
    color: '#fff',
  },
  secondary: {
    backgroundColor: theme.palette.secondary.main,
    color: '#fff',
  }
}))

export default function PhysiotherapistsList() {
  const classes = useStyles();
  const [allPhysiotherapistsList, setAllPhysiotherapistsList] = useState([]);
  const [physiotherapistsList, setPhysiotherapistsList] = useState([]);
  const [searched, setSearched] = useState('');
  const requestSearch = (searchedVal) => {
    let filteredPhysiotherapistsList = allPhysiotherapistsList.filter((row) => {
      return (
        row.displayName?.toLowerCase().includes(searchedVal.toLowerCase()) ||
        row.center?.toLowerCase().includes(searchedVal.toLowerCase()) ||
        row.remarks?.toLowerCase().includes(searchedVal.toLowerCase()) ||
        row.idNum?.toLowerCase().includes(searchedVal.toLowerCase())
      );
    });
    setPhysiotherapistsList(filteredPhysiotherapistsList);
  };
  const cancelSearch = () => {
    setSearched("");
    requestSearch(searched);
  };

  useEffect(() => {
    // get appointment data
    const requestOptions = {
      method: 'GET',
      headers: { 'Content-Type': 'application/json' },
    };
    var query = '';

    fetch('http://localhost:3001/physiotherapists', requestOptions)
      .then(response => response.json())
      .then(data => {
        setAllPhysiotherapistsList(data?.res);
        setPhysiotherapistsList(data?.res);
      });

  }, []);

  return (
    <>
      <PageTitle title="物理治療師列表" />
      <SearchBar
          value={searched}
          onChange={(searchVal) => requestSearch(searchVal)}
          onCancelSearch={() => cancelSearch()}
      />
      <Paper className={classes.iconsContainer}>
        <Table className="mb-0">
          <TableHead>
            <TableRow>
              {["日期", "時間", "診所", "物理治療師", "備註"].map(key => (
                <TableCell key={key}>{key}</TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {physiotherapistsList?.map(featureAppointment => (
              <TableRow>
                <TableCell className="pl-3 fw-normal">{featureAppointment.appointmentDate}</TableCell>
                <TableCell>{featureAppointment.appointmentTime}</TableCell>
                <TableCell>{featureAppointment.center}</TableCell>
                <TableCell>{featureAppointment.displayName}</TableCell>
                <TableCell>{featureAppointment.remarks}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    </>
  );
}
