import React, { useState } from "react";
import { Grid, TextField, Select, MenuItem, Box, FormControl, InputLabel,} from "@material-ui/core";
import { Close as CloseIcon } from "@material-ui/icons";
import { ToastContainer, toast } from "react-toastify";
import SyntaxHighlighter from "react-syntax-highlighter";
import { docco } from "react-syntax-highlighter/dist/esm/styles/hljs";
import classnames from "classnames";
import DatePicker from "react-date-picker";
import "react-datetime/css/react-datetime.css";




// styles
import "react-toastify/dist/ReactToastify.css";
import useStyles from "./styles";

// components
import Widget from "../../components/Widget/Widget";
import PageTitle from "../../components/PageTitle/PageTitle";
import Notification from "../../components/Notification";


const positions = [
  toast.POSITION.TOP_LEFT,
  toast.POSITION.TOP_CENTER,
  toast.POSITION.TOP_RIGHT,
  toast.POSITION.BOTTOM_LEFT,
  toast.POSITION.BOTTOM_CENTER,
  toast.POSITION.BOTTOM_RIGHT,
];


export default function NotificationsPage(props) {
  var classes = useStyles();

  // local
  var [notificationsPosition, setNotificationPosition] = useState(2);
  var [errorToastId, setErrorToastId] = useState(null);
  var [phy, setPhy] = useState('');
  var [bookDate, setBookDate] = useState(new Date());

  var hours = [10,11,12,13,14,15,16,17,18]
  var minutes = [0,30]

  var bookChange = (date) => {
    console.log("date: ", date);
    let formattedDate = `${
      date.getMonth() + 1
    }/${date.getDate()}/${date.getFullYear()}`;
    setBookDate(formattedDate);
  };

  return (
    <>
      <PageTitle title="預約診症" />
      <Grid container spacing={4}>
        <ToastContainer
          className={classes.toastsContainer}
          closeButton={
            <CloseButton className={classes.notificationCloseButton} />
          }
          closeOnClick={false}
          progressClassName={classes.notificationProgress}
        />
        <Grid item xs={12}>
        <Widget disableWidgetMenu>
          <Grid container item xs={12}>
          <div className={classes.formContainer}>
            <div className={classes.form}>
              <div>
                <TextField
                  id="outlined-name"
                  label="姓名"
                  value={localStorage.getItem('displayName')}
                  defaultValue="Hello World"
                  InputProps={{
                    readOnly: true,
                  }}
                />
              </div>
              <div>&nbsp;</div>
              <div>
                <Box sx={{ minWidth: 120 }}>
                  <FormControl fullWidth>
                    <InputLabel id="demo-simple-select-label">物理治療師</InputLabel>
                    <Select
                      labelId="demo-simple-select-label"
                      id="demo-simple-select"
                      value={phy}
                      label="Phy"
                      onChange={e => {setPhy(e.target.value)}}
                    >
                      <MenuItem value={10}>Ten</MenuItem>
                      <MenuItem value={20}>Twenty</MenuItem>
                      <MenuItem value={30}>Thirty</MenuItem>
                    </Select>
                  </FormControl>
                </Box>
              </div>
              <div>&nbsp;</div>
              <div>&nbsp;</div>
              <div>
                <DatePicker value={bookDate} onChange={(date) => {setBookDate(date)}}/>
              </div>
              <div>&nbsp;</div>
              <div>&nbsp;</div>
              <div>
                <Box sx={{ minWidth: 120 }}>
                  <FormControl>
                    <InputLabel id="demo-simple-select-label">時</InputLabel>
                    <Select
                      labelId="demo-simple-select-label"
                      id="demo-simple-select"
                      value={hour}
                      label="Hours"
                      onChange={e => {setHour(e.target.value)}}
                    >
                      {hours.map((value,index)=>{
                        return <MenuItem value={value}>{value}</MenuItem>
                      })}
                    </Select>
                  </FormControl>
                  <FormControl>
                    <InputLabel id="demo-simple-select-label">分</InputLabel>
                    <Select
                      labelId="demo-simple-select-label"
                      id="demo-simple-select"
                      value={minute}
                      label="Minutes"
                      onChange={e => {setMinute(e.target.value)}}
                    >
                      {minutes.map((value,index)=>{
                        return <MenuItem value={value}>{value}</MenuItem>
                      })}
                    </Select>
                  </FormControl>
                </Box>
              </div>
            </div>
          </div>
          </Grid>
          </Widget>
        </Grid>
      </Grid>
    </>
  );

}

function CloseButton({ closeToast, className }) {
  return <CloseIcon className={className} onClick={closeToast} />;
}
